  .coin::before,
  .coin__back,
  .coin__back::after,
  .coin__front,
  .coin__front::after,
  .coin__middle {
    border-radius: 50%;
    box-sizing: border-box;
    height: 100%;
    left: 0;
    position: absolute;
    width: 100%;
    z-index: 3;
