  .loading-bar {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 500px;
    height: 62.5px;
    transform: translate(-25%, -50%) scale(0.5);
    transition: all 0.5s;
    user-select: none;
    overflow: hidden;
  }

  .loading-bar img {
    position: absolute;
    top: 500px;
    left: 0;
    filter: drop-shadow(0 -500px 0 var(--loadingbar-background-color));
  }

  .loading-bar::after {
    content: "";
    position: absolute;
    top: 500px;
    left: 0;
    filter: drop-shadow(0 -500px 0 var(--loadingbar-prospect-color));
    width: 500px;
    height: 62.5px;
